import React from "react";
// import "./style.css"

function List(props) {
    return (
        props.books.map(book => {
            return (
                <div className="card">
                    <div className="card-body">
                        <div className="row">
                            <div className="col-lg-2 col-sm-7 col-xs-2">
                                <img src={book.image} alt="bookImage" />
                            </div>
                            <div className="col-lg-7 col-sm-11 col-xs-9">
                                <h2>{book.title}</h2>
                                <p>{book.authors}</p>
                                <p>{book.description}</p>
                                <button className="saveButton" key={book.id} onClick={() => props.handleSavedButton(book.id)}>Save</button>
                                <button><a className="viewButton" href={book.link} target="_blank" rel="noopener noreferrer">View</a></button>
                            </div>
                        </div>
                    </div>
                </div>
            );
        })
    );
}

export default List;
